/**
 * A prefix for use for custom event, attribute, and element names
 */
const APP_PREFIX = 'mvc';

/**
 * A custom attribute which directs the view to observe a particular value of the model
 */
export const MVC_OBSERVES = `${APP_PREFIX}-observes`;

/**
 * A custom attribute which directs the view to update a particular value of the model
 */
export const MVC_CONTROLS = `${APP_PREFIX}-controls`;

/**
 * A custom attribute which ensures that events are triggered properly for radio inputs
 */
export const MVC_RADIO_GROUP = `${APP_PREFIX}-radio-group`;

/**
 * A custom attribute which ensures that events are triggered properly for checkbox inputs
 */
export const MVC_CHECK_GROUP = `${APP_PREFIX}-check-group`;

/**
 * A custom event name for when multiple properties of a model have been modified
 */
export const MVC_MODEL_MODIFIED = `${APP_PREFIX}-model-modified`;

/**
 * A custom event name for when a single property of a model has been modified
 */
export const MVC_PROPERTY_CHANGED = `${APP_PREFIX}-property-changed`;

/**
 * A custom event name for when an input in a View has been updated
 */
export const MVC_INPUT_CHANGED = `${APP_PREFIX}-input-changed`;

/**
 * The name of the custom element defined by MVCView
 * @experimental this may be deprecated in the future
 */
export const MVC_VIEW_ELEMENT = `${APP_PREFIX}-view`;

import View from '../view';
import Model from '../model';

/**
 * This class is a custom HTMLElement that provides some syntactic sugar for setting up Views and Models
 * @experimental This class is an experimental featue, and may not be valid in the future
 */
export default class MVCView extends HTMLElement {
    /**
     * Simply call the super() constructor
     */
    constructor() {
        super();
    }

    /**
     * Adds a form around the content slot
     */
    connectedCallback() {
        /**
         * @type {object}
         */
        this._shadow = this.attachShadow({
            mode: 'open'
        });
        let formNode = document.createElement('form');
        let slot = document.createElement('slot');
        formNode.append(slot);
        this._shadow.appendChild(formNode);
    }

    /**
     * Return the model for the view. May be null if model has not been supplied yet.
     * @type {object}
     */
    get model() {
        return this._model;
    }

    /**
     * Set the model the view will observe and modify.
     * @param {object} model An object to be turned into an MVC Model
     */
    set model(model) {
        if (this._model) {
            throw new Error('The model may only be set once.');
        }

        /**
         * The Model instance encapsulated by this element
         * @type {object}
         */
        this._model = new Model(model);

        /**
         * The View instance encapsulated by this element
         * @type {object}
         */
        this._view = new View(this, this._model);
    }
}

/**
 * The EventEmitter class is modified from a snippet by MDN Contributers at 
 * https://developer.mozilla.org/en-US/docs/Web/API/EventTarget#_Simple_implementation_of_EventTarget 
 * Any copyright is dedicated to the Public Domain. http://creativecommons.org/publicdomain/zero/1.0/ 
 *
 * This enables custom classes to dispatch events, and is used by micro-mvc to establish observers
 * and fulfill the Model-View-Controller design pattern
 */

export default class EventEmitter {
    /**
     * This class is intended to be extended or composed into other classes.
     */
    constructor() {
        /**
         * Listeners for each event type
         * @type {object}
         */
        this.listeners = {};
    }

    /**
     * Add an event listener
     * @param {string} type The name of the event
     * @param {function} callback The function to execute upon occurence of the event
     */
    addEventListener(type, callback) {
        if (!(type in this.listeners)) {
            this.listeners[type] = [];
        }
        this.listeners[type].push(callback);
    }

    /**
     * Remove an even listner
     * @param {string} type The name of the event
     * @param {function} callback The function to stop executing upon occurence of the event
     */
    removeEventListener(type, callback) {
        if (!(type in this.listeners)) {
            return;
        }
        var stack = this.listeners[type];
        for (var i = 0, l = stack.length; i < l; i++) {
            if (stack[i] === callback) {
                stack.splice(i, 1);
                return;
            }
        }
    }

    /**
     * Dispatch an event. Upon dispatching an event, all listeners are called
     * @param {object} event The `Event` that needs to be dispatched.
     * @return {boolean} returns the inverse of `event.defaultPrevented`
     */
    dispatchEvent(event) {
        if (!(event.type in this.listeners)) {
            return true;
        }
        var stack = this.listeners[event.type].slice();

        for (var i = 0, l = stack.length; i < l; i++) {
            stack[i].call(this, event);
        }
        return !event.defaultPrevented;
    }

    /**
     * Check whether the instance has listeners for the provided event type
     * @return {boolean} true if there is a listener for this event type, otherwise false
     * @param {string} type The type of event to check for
     */
    listensFor(type) {
        return !!this.listeners[type];
    }

    /**
     * Check whether the instance has a specific listener for the provided event type
     * @return {boolean} true if the callback exists for this event type, otherwise false
     * @param {string} type The type of event to check for
     * @param {function} callback The specific callback to check for
     */
    hasListener(type, callback) {
        return !!this.listeners[type] ? this.listeners[type].indexOf(callback) >= 0 : false;
    }
}

import Model from './model';
import View from './view';
import EventEmitter from './event-emitter';
import ViewElement from './custom-elements/mvc-view';

import {
    MVC_VIEW_ELEMENT
} from './config';

//define the custom element
//WARNING: this may be deprecated in the future
window.customElements.define(MVC_VIEW_ELEMENT, ViewElement);

export {
    Model,
    View,
    EventEmitter
};

import EventEmitter from './event-emitter';

import * as config from './config';

/**
 * This class encapsulates a basic Object and dispatches events when they are changed
 * @extends {EventEmitter}
 */
export default class Model extends EventEmitter {
    /**
     * Create a Model which can observe and be observed by supplying a raw data object and encapsulating it
     * @param {object} dataModel The raw data object to be encapsulated by the instance
     */
    constructor(dataModel) {
        super();

        /**
         * the raw data object which is encapsulated by the event emitter
         * @type {object}
         */
        this._dataModel = dataModel;

        for (let k of Object.keys(this._dataModel)) {

            Object.defineProperty(this, k, {
                innumerable: true,
                get: () => {
                    return this._dataModel[k];
                },
                set: (val) => {
                    dataModel[k] = val;
                    let e = new CustomEvent(config.MVC_PROPERTY_CHANGED, {
                        detail: {
                            model: this._dataModel,
                            property: k,
                            value: val
                        }
                    });
                    this.dispatchEvent(e);
                }
            });
        }
    }

    /**
     * return the underlying _dataModel in its current state
     * @type {object}
     * @deprecated Use `dataModel` instead
     */
    get model() {
        return this._dataModel;
    }

    /**
     * return the underlying _dataModel in its current state
     * @type {object}
     * @experimental This may be deprecated in the future to preserve the object's namespace
     */
    get dataModel() {
        return this._dataModel;
    }

    /**
     * assign multiple values to the dataModel, and dispatch an event.
     * @param {object} val An object containing the values to be updated
     * @experimental This may be deprecated in the future to preserve the object's namespace
     */
    modify(val) {
        Object.assign(this._dataModel, val);
        //In some situations, it may be too computationally
        //intensive to make updates to all listeners to model,
        //so providing a list of the properties and values that
        //were changed and the raw val object as "updates" so
        //that only the updates are necessary to process
        let e = new CustomEvent(config.MVC_MODEL_MODIFIED, {
            detail: {
                updates: val,
                model: this._dataModel,
                properties: [Object.keys(val)],
                values: [Object.values(val)]
            }
        });

        this.dispatchEvent(e);
    }
}

import * as config from './config';

import EventEmitter from './event-emitter';

/**
 * The View class is an EventEmitter that searches the children of the provided DOM object for custom attributes
 * which direct it to observe and/or update properties of the provided model object
 * @extends {EventEmitter}
 * @todo Write detailed comments
 */
export default class View extends EventEmitter {
    /**
     * The constructor searches the DOM object and attaches events to the model and the view
     * @param {object} viewDOM A DOM object with children that observe or control the values of the model
     * @param {object} model A model object of type Model to be observed and updated by the View
     */
    constructor(viewDOM, model) {
        super();

        let observers = viewDOM.querySelectorAll(`[${config.MVC_OBSERVES}]`);
        let controls = viewDOM.querySelectorAll(`[${config.MVC_CONTROLS}]`);

        let radioGroupObservers = viewDOM.querySelectorAll(`[${config.MVC_OBSERVES}][${config.MVC_RADIO_GROUP}]`);
        let radioGroupControls = viewDOM.querySelectorAll(`[${config.MVC_CONTROLS}][${config.MVC_RADIO_GROUP}]`);

        let checkGroupObservers = viewDOM.querySelectorAll(`[${config.MVC_OBSERVES}][${config.MVC_CHECK_GROUP}]`);
        let checkGroupControls = viewDOM.querySelectorAll(`[${config.MVC_CONTROLS}][${config.MVC_CHECK_GROUP}]`);

        radioGroupObservers = Array.prototype.map.call(radioGroupObservers, (o) => o);
        radioGroupControls = Array.prototype.map.call(radioGroupControls, (c) => c);

        checkGroupObservers = Array.prototype.map.call(checkGroupObservers, (o) => o);
        checkGroupControls = Array.prototype.map.call(checkGroupControls, (c) => c);

        let fieldsetGetAdapter = new Map();
        let fieldsetOptionsMap = new Map();
        let selectOptionsMap = new Map();
        let fieldsetSetAdapter = new Map();

        function getInputValue(control) {
            let inputType = control.getAttribute('type');

            //most of these can just fall through to default, listing them exhaustively for thoroughness
            switch (inputType) {
                case 'button':
                case 'file': //TODO: figure out if possible to handle file
                case 'image':
                case 'password': //you should never be allowed to control this programmatically
                case 'reset':
                case 'submit':
                    throw new TypeError(`Inputs of type ${inputType} cannot observe models`);
                case 'checkbox':
                    return control.checked;
                    //case 'color': case 'date': case 'datetime-local': case 'email': case 'hidden': case 'month': case 'number': case 'radio': case 'range': case 'search': case 'tel': case 'text': case 'time': case 'url': case 'week':
                default:
                    return control.value;
            }
        }

        function getControlValue(control) {
            switch (control.tagName) {
                case 'INPUT':
                    return getInputValue(control);
                case 'FIELDSET':
                    return fieldsetGetAdapter.get(control)();
                case 'SELECT':
                    return control.value;
                default:
                    return control.innerHTML;
            }
        }

        function updateInputValue(observer, value) {
            let inputType = observer.getAttribute('type');
            switch (inputType) {
                case 'button':
                case 'file': //TODO: figure out if possible to handle file
                case 'image':
                case 'reset':
                case 'submit':
                    throw new TypeError(`Inputs of type ${inputType} do not have values`);
                case 'password': //you should never be allowed to control this programmatically
                    throw new TypeError(`Inputs of type ${inputType} may not be modified by controllers`);
                case 'checkbox':
                    observer.checked = value;
                    break;
                    //TODO: implement errors for cases when types like numbers and ranges have min, max, and step attributes
                    //TODO: test dates
                    //case 'color': case 'date': case 'datetime-local': case 'email': case 'hidden': case 'month': case 'number': case 'radio': case 'range': case 'search': case 'tel': case 'text': case 'time': case 'url': case 'week':
                default:
                    observer.value = value;
                    break;
            }
        }

        function updateObserver(observer, value) {
            switch (observer.tagName) {
                case 'INPUT':
                    updateInputValue(observer, value);
                    break;
                case 'FIELDSET':
                    if (observer.hasAttribute(config.MVC_RADIO_GROUP) && !fieldsetOptionsMap.get(observer).includes(value)) {
                        throw new RangeError(`The value supplied, ${value}, is not a valid option`);
                    } else if (observer.hasAttribute(config.MVC_CHECK_GROUP) && Object.keys(value).filter(v => !fieldsetOptionsMap.get(observer).includes(v)).length) {
                        throw new RangeError(`At least one of the values supplied, ${value}, is not a valid option`);
                    }
                    fieldsetSetAdapter.get(observer)(value);
                    break;
                case 'SELECT':
                    if (!selectOptionsMap.get(observer).includes(value)) {
                        throw new RangeError(`The value supplied, ${value}, is not a valid option`);
                    }
                    observer.value = value;
                    break;
                default:
                    observer.innerHTML = value;
                    break;
            }
        }

        for (let i = 0; i < controls.length; i++) {
            let controlledValues = controls[i].getAttribute(config.MVC_CONTROLS).split(',');
            let addEventListeners = (c) => {
                for (let val in controlledValues) {
                    c.addEventListener('change', (e) => {
                        let ce = new CustomEvent(config.MVC_INPUT_CHANGED, {
                            target: e.target,
                            detail: {
                                property: controlledValues[val],
                                value: getControlValue(controls[i])
                            }
                        });
                        model[controlledValues[val]] = getControlValue(controls[i]);
                        this.dispatchEvent(ce);
                    });
                }
            };

            if (radioGroupControls.includes(controls[i])) {
                let radioGroupName = controls[i].getAttribute(config.MVC_RADIO_GROUP);

                let radioForm = controls[i].closest('form');
                let nodeList = radioForm ? radioForm.elements[radioGroupName] : document.getElementsByName(radioGroupName);

                fieldsetGetAdapter.set(controls[i], (radioForm ?
                    () => nodeList.value :
                    () => controls[i].querySelector(':checked').value
                ));

                nodeList.forEach((n) => {
                    addEventListeners(n);
                });
            } else if (checkGroupControls.includes(controls[i])) {
                let nodeList = controls[i].querySelectorAll('[type="checkbox"]');

                fieldsetGetAdapter.set(controls[i], () => {
                    let result = {};
                    nodeList.forEach((n) => {
                        result[n.value] = n.checked;
                    });
                    return result;
                });

                nodeList.forEach((n) => {
                    addEventListeners(n);
                });
            } else {
                addEventListeners(controls[i]);
            }
        }

        for (let i = 0; i < observers.length; i++) {
            if (radioGroupObservers.includes(observers[i])) {
                let radioGroupName = observers[i].getAttribute(config.MVC_RADIO_GROUP);

                let radioForm = observers[i].closest('form');
                let nodeList = radioForm ? radioForm.elements[radioGroupName] : document.getElementsByName(radioGroupName);
                fieldsetSetAdapter.set(observers[i], (radioForm ?
                    (val) => nodeList.value = val :
                    (val) => nodeList.forEach((n) => {
                        n.checked = false;
                        if (n.value === val) {
                            n.checked = true;
                        }
                    })));
                let options = [];
                nodeList.forEach((o) => {
                    options.push(o.value);
                });
                fieldsetOptionsMap.set(observers[i], options);
            } else if (checkGroupObservers.includes(observers[i])) {
                let nodeList = observers[i].querySelectorAll('[type="checkbox"]');
                fieldsetSetAdapter.set(observers[i], (val) => {
                    nodeList.forEach((n) => {
                        n.checked = val[n.value];
                    });
                });
                let options = [];
                nodeList.forEach((o) => {
                    options.push(o.value);
                });
                fieldsetOptionsMap.set(observers[i], options);
            } else if (observers[i].tagName === 'SELECT') {
                let options = [];
                observers[i].querySelectorAll('option').forEach((o) => {
                    options.push(o.value);
                });
                selectOptionsMap.set(observers[i], options);
            }

            let observedValues = observers[i].getAttribute(config.MVC_OBSERVES).split(',');
            for (let val in observedValues) {
                model.addEventListener(config.MVC_PROPERTY_CHANGED, (e) => {
                    if (observedValues[val] === e.detail.property && e.target !== observers[i]) {
                        updateObserver(observers[i], e.detail.value);
                    }
                });
            }
        }

        model.addEventListener(config.MVC_MODEL_MODIFIED, (e) => {
            for (let i = 0; i < observers.length; i++) {
                let observedValues = observers[i].getAttribute(config.MVC_OBSERVES).split(',');
                for (let val in observedValues) {
                    let updatedVal = e.detail.updates[observedValues[val]];
                    if (updatedVal) {
                        updateObserver(observers[i], updatedVal);
                    }
                }
            }
        });

        //Assume that view DOM object is empty, and trigger a model update to ensure data is injected properly
        model.modify(model.dataModel);
    }
}

